import React from 'react';
import { Box, Paper, Button } from '@mui/material';
import { keyframes } from '@mui/system';

const fallAnimation = keyframes`
  from {
    transform: translateY(-400px);
  }
  to {
    transform: translateY(0);
  }
`;

const GameBoard = ({ board, onColumnClick, gameOver, winner, onReset }) => {
  const handleColumnClick = (column) => {
    console.log('Column clicked:', column);
    if (!gameOver) {
      onColumnClick(column);
    }
  };

  return (
    <Box sx={{ position: 'relative', mb: 4 }}>
      <Paper 
        elevation={3} 
        sx={{ 
          p: 2, 
          bgcolor: 'background.paper',
          position: 'relative',
          overflow: 'visible'
        }}
      >
        <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(7, 1fr)', gap: 1 }}>
          {board[0].map((_, columnIndex) => (
            <Box
              key={columnIndex}
              onClick={() => handleColumnClick(columnIndex)}
              sx={{
                width: 60,
                height: 60,
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                cursor: gameOver ? 'default' : 'pointer',
                '&:hover': {
                  bgcolor: gameOver ? 'transparent' : 'action.hover',
                },
              }}
            >
              <Box
                sx={{
                  width: '100%',
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center',
                }}
              >
                {board.map((row, rowIndex) => {
                  const cell = row[columnIndex];
                  if (cell === 0) return null;

                  return (
                    <Box
                      key={rowIndex}
                      sx={{
                        width: 50,
                        height: 50,
                        borderRadius: '50%',
                        bgcolor: cell === 1 ? 'primary.main' : 'secondary.main',
                        animation: `${fallAnimation} 0.6s cubic-bezier(0.4, 0, 0.2, 1)`,
                        position: 'absolute',
                        top: rowIndex * 60 + 20,
                        left: columnIndex * 60 + 20,
                        zIndex: 1,
                      }}
                    />
                  );
                })}
              </Box>
            </Box>
          ))}
        </Box>
      </Paper>

      {(gameOver || winner !== null) && (
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={onReset}
            sx={{ mt: 2 }}
          >
            New Game
          </Button>
        </Box>
      )}
    </Box>
  );
};

export default GameBoard;
