import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Container, Box, Typography } from '@mui/material';
import GameBoard from './components/GameBoard';
import axios from 'axios';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#2196f3',
    },
    secondary: {
      main: '#f50057',
    },
  },
});

function App() {
  const [board, setBoard] = useState(Array(6).fill(Array(7).fill(0)));
  const [gameOver, setGameOver] = useState(false);
  const [winner, setWinner] = useState(null);

  const fetchBoardState = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/board');
      const boardState = response.data.split(',').map(Number);
      const newBoard = [];
      for (let i = 0; i < 6; i++) {
        newBoard.push(boardState.slice(i * 7, (i + 1) * 7));
      }
      setBoard(newBoard);
    } catch (error) {
      console.error('Error fetching board state:', error);
    }
  };

  const makeMove = async (col) => {
    if (gameOver) return;
    try {
      await axios.post('http://localhost:8080/api/move', { column: col });
      await fetchBoardState();
    } catch (error) {
      console.error('Error making move:', error);
    }
  };

  const resetGame = async () => {
    try {
      await axios.post('http://localhost:8080/api/reset');
      setGameOver(false);
      setWinner(null);
      await fetchBoardState();
    } catch (error) {
      console.error('Error resetting game:', error);
    }
  };

  useEffect(() => {
    fetchBoardState();
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="sm">
        <Box sx={{ my: 4, textAlign: 'center' }}>
          <Typography variant="h2" component="h1" gutterBottom>
            Connect 4
          </Typography>
          <GameBoard
            board={board}
            onColumnClick={makeMove}
            gameOver={gameOver}
            winner={winner}
            onReset={resetGame}
          />
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
