import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Container, Box, Typography, Alert, Button } from '@mui/material';
import GameBoard from './components/GameBoard';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#ffeb3b', // Yellow for player 1
    },
    secondary: {
      main: '#f50057', // Red for player 2 (AI)
    },
  },
});

function App() {
  const [board, setBoard] = useState(Array(6).fill().map(() => Array(7).fill(0)));
  const [gameOver, setGameOver] = useState(false);
  const [winner, setWinner] = useState(0);
  const [error, setError] = useState(null);
  const [isAIMove, setIsAIMove] = useState(false);
  const [winningPositions, setWinningPositions] = useState([]);

  useEffect(() => {
    fetchBoardState();
  }, []);

  const fetchBoardState = async () => {
    try {
      const response = await fetch('http://localhost:8080/board');
      const data = await response.text();
      const parts = data.split('|');
      const boardState = parts[0];
      const [isOver, winnerValue] = parts[1].split(',').map(Number);

      // Parse board state
      const boardArray = boardState.split(',')
        .filter(cell => cell !== '')
        .map(Number);

      const newBoard = [];
      for (let i = 0; i < 6; i++) {
        newBoard.push(boardArray.slice(i * 7, (i + 1) * 7));
      }
      setBoard(newBoard);

      // Parse game state
      setGameOver(isOver === 1);
      setWinner(winnerValue);

      // Parse winning positions if they exist
      if (parts.length > 2 && isOver === 1 && winnerValue !== 0) {
        const positions = parts[2].split(',')
          .filter(pos => pos !== '')
          .map(Number);
        const winningPos = [];
        for (let i = 0; i < positions.length; i += 2) {
          winningPos.push([positions[i], positions[i + 1]]);
        }
        setWinningPositions(winningPos);
      } else {
        setWinningPositions([]);
      }

      // Return whether the game is over
      return isOver === 1;
    } catch (error) {
      console.error('Error fetching board state:', error);
      return false;
    }
  };

  const makeMove = async (column) => {
    if (gameOver || isAIMove) return;

    // Immediately set isAIMove to true to prevent double clicks
    setIsAIMove(true);

    try {
      // Update board preview locally for immediate feedback
      const newBoard = [...board];
      let validMove = false;
      // Find lowest empty row in selected column
      for (let row = 5; row >= 0; row--) {
        if (newBoard[row][column] === 0) {
          // Create a copy of the board and place the player's piece
          newBoard[row][column] = 1; // Player is 1
          setBoard(newBoard);
          validMove = true;
          break;
        }
      }

      if (!validMove) {
        setIsAIMove(false);
        return;
      }

      // Now send the move to the server
      const formData = new URLSearchParams();
      formData.append('column', column);

      const response = await fetch('http://localhost:8080/move', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: formData.toString(),
      });

      if (!response.ok) {
        throw new Error('Move failed');
      }

      // Get latest board state from server and check if game is over
      const isGameOver = await fetchBoardState();

      // Check if the game is over after player's move
      if (isGameOver) {
        setIsAIMove(false);
        return;
      }

      // Add a delay before AI moves
      setTimeout(() => {
        makeAIMove();
      }, 1000);
    } catch (error) {
      console.error('Error making move:', error);
      // Refresh board state to correct any inconsistencies
      fetchBoardState();
      setIsAIMove(false);
    }
  };

  const makeAIMove = async () => {
    // This check is a safeguard, but should be redundant due to the checks in makeMove
    if (gameOver) {
      setIsAIMove(false);
      return;
    }

    try {
      console.log("AI making move...");

      const response = await fetch('http://localhost:8080/ai-move', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
      });

      if (!response.ok) {
        throw new Error('AI move failed');
      }

      // Update the board with the AI's move
      await fetchBoardState();

      // Now that AI move is complete, re-enable player moves
      setIsAIMove(false);
    } catch (error) {
      console.error('Error making AI move:', error);
      // Refresh board state even if there was an error
      fetchBoardState();
      setIsAIMove(false);
    }
  };

  const resetGame = async () => {
    try {
      const response = await fetch('http://localhost:8080/reset', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
      });

      if (!response.ok) {
        throw new Error('Reset failed');
      }

      await fetchBoardState();
      setGameOver(false);
      setWinner(0);
      setIsAIMove(false);
      setWinningPositions([]);
    } catch (error) {
      console.error('Error resetting game:', error);
    }
  };

  const getGameStatus = () => {
    if (gameOver && winner !== 0) {
      return winner === 1 ? "You Won! ðŸŽ‰" : "AI Won! ðŸ¤–";
    }
    return isAIMove ? "AI is thinking... ðŸ¤”" : "Your turn - Drop a piece! ðŸ‘‡";
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="sm">
        <Box sx={{ 
          display: 'flex', 
          flexDirection: 'column', 
          alignItems: 'center', 
          minHeight: '100vh',
          py: 4
        }}>
          <Typography variant="h3" component="h1" gutterBottom>
            Connect 4
          </Typography>

          <Typography 
            variant="h5" 
            color="primary" 
            sx={{ 
              mb: 3,
              fontWeight: 'bold',
              textAlign: 'center'
            }}
          >
            {getGameStatus()}
          </Typography>

          <GameBoard 
            board={board} 
            onColumnClick={makeMove}
            gameOver={gameOver}
            winner={winner}
            onReset={resetGame}
            winningPositions={winningPositions}
          />
        </Box>
      </Container>
    </ThemeProvider>
  );
}

export default App;
