import React from 'react';
import { Box, Paper, Button } from '@mui/material';
import { keyframes } from '@mui/system';

const fallAnimation = keyframes`
  from {
    transform: translateY(-400px);
  }
  to {
    transform: translateY(0);
  }
`;

const GameBoard = ({ board, onColumnClick, gameOver, winner, onReset }) => {
  const handleColumnClick = (column) => {
    console.log('Column clicked:', column);
    if (!gameOver) {
      onColumnClick(column);
    }
  };

  // Create a reversed copy of the board to display from bottom to top
  const displayBoard = [...board].reverse();

  return (
    <Box sx={{ position: 'relative', mb: 4 }}>
      <Paper 
        elevation={3} 
        sx={{ 
          p: 2, 
          bgcolor: '#1a1a1a',
          position: 'relative',
          overflow: 'visible',
          borderRadius: 2
        }}
      >
        <Box sx={{ 
          display: 'grid', 
          gridTemplateRows: 'repeat(6, 1fr)',
          gap: 1,
          width: 'fit-content',
          margin: '0 auto'
        }}>
          {displayBoard.map((row, rowIndex) => (
            <Box
              key={rowIndex}
              sx={{
                display: 'grid',
                gridTemplateColumns: 'repeat(7, 1fr)',
                gap: 1
              }}
            >
              {row.map((cell, columnIndex) => (
                <Box
                  key={`${rowIndex}-${columnIndex}`}
                  onClick={() => handleColumnClick(columnIndex)}
                  sx={{
                    width: 60,
                    height: 60,
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    cursor: gameOver ? 'default' : 'pointer',
                    '&:hover': {
                      bgcolor: gameOver ? 'transparent' : 'rgba(255, 255, 255, 0.1)',
                    },
                    position: 'relative'
                  }}
                >
                  {/* Empty cell background */}
                  <Box
                    sx={{
                      width: 50,
                      height: 50,
                      borderRadius: '50%',
                      bgcolor: '#424242',
                      position: 'absolute'
                    }}
                  />
                  {/* Game piece */}
                  {cell !== 0 && (
                    <Box
                      sx={{
                        width: 50,
                        height: 50,
                        borderRadius: '50%',
                        bgcolor: cell === 1 ? 'primary.main' : 'secondary.main',
                        animation: `${fallAnimation} 0.6s cubic-bezier(0.4, 0, 0.2, 1)`,
                        position: 'absolute',
                        zIndex: 1,
                      }}
                    />
                  )}
                </Box>
              ))}
            </Box>
          ))}
        </Box>
      </Paper>

      {(gameOver || winner !== null) && (
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={onReset}
            sx={{ mt: 2 }}
          >
            New Game
          </Button>
        </Box>
      )}
    </Box>
  );
};

export default GameBoard;
