import React from 'react';
import { Box, Paper, Button } from '@mui/material';
import { keyframes } from '@mui/system';

const fallAnimation = keyframes`
  from {
    transform: translateY(-100%);
  }
  to {
    transform: translateY(0);
  }
`;

const GameBoard = ({ board, onColumnClick, gameOver, winner, onReset }) => {
  const getCellColor = (value) => {
    switch (value) {
      case 1:
        return '#ffeb3b'; // Yellow for player 1
      case 2:
        return '#f50057'; // Red for player 2 (AI)
      default:
        return '#424242'; // Dark gray for empty
    }
  };

  const handleColumnClick = (col) => {
    if (!gameOver && onColumnClick) {
      onColumnClick(col);
    }
  };

  return (
    <Paper 
      elevation={3} 
      sx={{ 
        p: 2, 
        display: 'inline-block',
        backgroundColor: '#1a1a1a',
        borderRadius: 2
      }}
    >
      <Box sx={{ display: 'grid', gap: 1 }}>
        {board.map((row, rowIndex) => (
          <Box key={rowIndex} sx={{ display: 'flex', gap: 1 }}>
            {row.map((cell, colIndex) => (
              <Box
                key={`${rowIndex}-${colIndex}`}
                onClick={() => handleColumnClick(colIndex)}
                sx={{
                  width: 50,
                  height: 50,
                  backgroundColor: getCellColor(cell),
                  borderRadius: '50%',
                  cursor: gameOver ? 'default' : 'pointer',
                  transition: 'background-color 0.3s',
                  animation: cell !== 0 ? `${fallAnimation} 0.5s ease-out` : 'none',
                  '&:hover': {
                    backgroundColor: gameOver ? getCellColor(cell) : '#666',
                  },
                }}
              />
            ))}
          </Box>
        ))}
      </Box>

      {(gameOver || winner) && (
        <Box sx={{ mt: 2, textAlign: 'center' }}>
          <Button
            variant="contained"
            color="primary"
            onClick={onReset}
            sx={{ mt: 2 }}
          >
            New Game
          </Button>
        </Box>
      )}
    </Paper>
  );
};

export default GameBoard;
